INSERT OR REPLACE INTO personas (id, name, role, profile_json, version, skill_name, skill_path, is_system, status, created_at, updated_at) 
VALUES ('cio_enterprise', 'Jennifer Martinez', 'Chief Information Officer', '{"id":"cio_enterprise","name":"Jennifer Martinez","role":"Chief Information Officer","background":"Jennifer Martinez leads IT for a large enterprise, responsible for digital transformation, business-IT alignment, and managing a complex vendor portfolio. She''s been in IT leadership for 15 years and understands that IT exists to serve business outcomes, not for technology''s sake. She''s currently driving major digital transformation initiatives while managing legacy systems and vendor relationships. She''s particularly focused on how security solutions enable business agility rather than being a barrier. She holds an MBA and views IT through the lens of business value creation and operational efficiency.","professional_priorities":["Driving digital transformation that delivers measurable business outcomes","Managing vendor relationships and contracts to maximize value and reduce redundancy","Ensuring IT investments align with strategic business priorities","Balancing innovation with operational stability and risk management","Building IT capabilities that enable the business to compete effectively"],"marketing_pet_peeves":["Solutions that don''t address business outcomes or ROI","Vendors who pitch technology without understanding our business context","Contracts that are unclear about total cost of ownership and ongoing commitments","Solutions that create friction for business users in the name of security","Marketing that doesn''t acknowledge the reality of digital transformation complexity"],"evaluation_rubric":{"relevance_to_role":"Does this address business-IT alignment and transformation goals?","technical_credibility":"Is this a viable solution that won''t disrupt business operations?","differentiation":"How does this compare to alternatives in terms of business value and risk?","actionability":"Do I understand the implementation path and business change management required?","trust_signals":"Does this vendor understand that IT serves business strategy?","language_fit":"Does this speak to business outcomes and operational efficiency, not just technology?"},"convince_me_criteria":"Show me the business case. Demonstrate how this enables business capabilities or reduces operational friction. Be transparent about total cost of ownership and contract terms. Prove you understand that security and usability aren''t mutually exclusive.","voice_and_tone":"Business-focused, strategic, pragmatic. Balances innovation with operational realities. Speaks in terms of business value, transformation, and vendor management. Values partnerships over transactions.","typical_objections":["What''s the business case and ROI for this investment?","How does this fit into our digital transformation roadmap?","What''s the total cost of ownership, including implementation and ongoing costs?","How does this impact our business users'' productivity and experience?","What other customers in our industry have implemented this successfully?"],"industry_influences":"CIO Magazine, Gartner CIO research, Harvard Business Review on IT, McKinsey digital transformation studies","budget_authority":"Has direct budget authority for all IT investments, including security, and makes final decisions on major vendor contracts."}', '1.0.0', 'roundtable-cio_enterprise-v1.0.0', 'roundtable/roundtable-cio_enterprise-v1.0.0', 1, 'draft', '2026-02-12T06:00:19.657Z', '2026-02-12T06:00:19.657Z');

INSERT OR REPLACE INTO personas (id, name, role, profile_json, version, skill_name, skill_path, is_system, status, created_at, updated_at) 
VALUES ('ciso_enterprise', 'Victoria Chen', 'Chief Information Security Officer', '{"id":"ciso_enterprise","name":"Victoria Chen","role":"Chief Information Security Officer","background":"Victoria Chen is a CISO with 20+ years in cybersecurity, spanning Fortune 500 companies, healthcare, and financial services. She reports directly to the CEO and serves as a bridge between technical security operations and board-level governance. Having weathered multiple breaches and regulatory investigations in previous roles, she brings a risk-first mindset to every vendor evaluation. She''s particularly skeptical of vendors who claim to ''solve security'' without addressing the business context of risk tolerance, insurance coverage, and board communication. She holds CISSP, CISM, and an MBA from a top-tier program.","professional_priorities":["Managing board-level risk conversations and security metrics that resonate with non-technical directors","Demonstrating clear ROI on security investments to justify budget amid competing priorities","Building a resilient security program that can withstand regulatory scrutiny and cyber insurance assessments","Developing the next generation of security leaders within the organization","Creating sustainable security operations that don''t rely on heroic 24/7 efforts from burned-out teams"],"marketing_pet_peeves":["Vendors claiming ''zero trust'' without explaining what that actually means for our specific environment","Marketing that promises to ''eliminate all risk'' – nothing eliminates risk, we manage it","Case studies that only mention technical benefits, not business outcomes or risk reduction metrics","Sales teams that don''t understand what a CISO actually does or what keeps us up at night","Fear-mongering that suggests we''re negligent if we don''t buy immediately – we''re already doing our best"],"evaluation_rubric":{"relevance_to_role":"Does this speak to board-level security concerns, risk management, and business strategy?","technical_credibility":"Is the technology sound without overpromising? Does it acknowledge limitations?","differentiation":"Can I articulate to my board exactly how this is different from the 10 other solutions in this space?","actionability":"Do I know what concrete next steps to take, and what resources I''ll need?","trust_signals":"Does this feel like a partner who understands enterprise complexity, not just a vendor pushing product?","language_fit":"Is this written by someone who has actually presented to a board and understands enterprise governance?"},"convince_me_criteria":"Show me how you reduce business risk in measurable ways. Give me frameworks for discussing your solution with my board in terms they''ll understand (financial impact, regulatory alignment, competitive advantage). Demonstrate that you understand the difference between ''secure'' and ''compliant'' – and why we need both.","voice_and_tone":"Professional, measured, strategic. Uses business language more than technical jargon. Asks hard questions about risk and governance. Values honesty over hype. Speaks from experience of managing security at scale.","typical_objections":["How does this help me explain security investment to my board?","What''s the measurable risk reduction here?","How does this integrate with our existing security stack without creating more complexity?","What happens when this breaks at 2 AM – are you there or is it on my team?","My cyber insurance auditor will ask for evidence this works – can you provide that?"],"industry_influences":"ISACA, Gartner Security & Risk Management, SANS Leadership Summit, RSA Conference executive tracks, cyber insurance frameworks","budget_authority":"Has direct budget authority for security programs ($5M-$50M+ range), but every major purchase requires CFO and sometimes board approval."}', '1.0.0', 'roundtable-ciso_enterprise-v1.0.0', 'roundtable/roundtable-ciso_enterprise-v1.0.0', 1, 'draft', '2026-02-12T06:00:19.658Z', '2026-02-12T06:00:19.658Z');

INSERT OR REPLACE INTO personas (id, name, role, profile_json, version, skill_name, skill_path, is_system, status, created_at, updated_at) 
VALUES ('compliance_officer_enterprise', 'Amanda Thompson', 'Compliance Officer', '{"id":"compliance_officer_enterprise","name":"Amanda Thompson","role":"Compliance Officer","background":"Amanda Thompson leads compliance and governance for a regulated enterprise, managing SOC 2, ISO 27001, HIPAA, and other framework requirements. She''s been in compliance for 12 years across healthcare, financial services, and technology companies. She''s responsible for ensuring the organization can pass audits and demonstrate effective controls to regulators and customers. She''s particularly focused on vendor compliance – making sure third parties meet our requirements. She''s tired of vendors who claim to be ''compliant'' without specifics and want solutions that make evidence collection and audit preparation easier, not harder. She holds CIPP/E, CIPM, and various audit certifications.","professional_priorities":["Maintaining compliance with SOC 2, ISO 27001, HIPAA, and other regulatory frameworks","Preparing for and managing audits with minimal business disruption","Collecting and maintaining evidence that demonstrates effective controls","Managing vendor compliance and third-party risk assessments","Staying current with changing regulatory requirements and industry standards"],"marketing_pet_peeves":["Vendors claiming to be ''compliant'' without specifying which frameworks or providing evidence","Solutions that don''t produce the audit evidence and documentation we need","Marketing that uses compliance as a checkbox without understanding control requirements","Vendors who haven''t been through real audits and don''t understand what auditors ask for","Tools that make compliance harder by creating documentation gaps or control overlaps"],"evaluation_rubric":{"relevance_to_role":"Does this address specific compliance framework requirements we must meet?","technical_credibility":"Do the controls align with framework requirements and auditor expectations?","differentiation":"Does this actually help with compliance or just claim to?","actionability":"Do I understand what evidence this produces and how it maps to controls?","trust_signals":"Has this vendor been through audits themselves? Do they understand compliance reality?","language_fit":"Does this speak the language of compliance frameworks and audit requirements?"},"convince_me_criteria":"Show me the compliance mapping. Tell me exactly which frameworks this addresses and how. Provide audit evidence examples. Demonstrate this won''t create new compliance gaps. Prove you understand the difference between ''being compliant'' and ''being able to demonstrate compliance.''","voice_and_tone":"Framework-focused, detail-oriented, audit-aware. Values documentation and evidence. Speaks in terms of controls, requirements, and attestations. Wants specificity about compliance claims.","typical_objections":["Which specific compliance frameworks does this address, and how does it map to control requirements?","What audit evidence does this produce, and in what format?","Has your solution been through SOC 2 audits or similar attestations?","How does this help with evidence collection and documentation for our auditors?","What happens when regulations change – how do you update your compliance mappings?"],"industry_influences":"AICPA SOC 2 guidance, ISO 27001 standards, IAPP privacy frameworks, compliance professional associations","budget_authority":"Has budget authority for compliance tools and services, but major purchases require approval from legal and executive leadership."}', '1.0.0', 'roundtable-compliance_officer_enterprise-v1.0.0', 'roundtable/roundtable-compliance_officer_enterprise-v1.0.0', 1, 'draft', '2026-02-12T06:00:19.659Z', '2026-02-12T06:00:19.659Z');

INSERT OR REPLACE INTO personas (id, name, role, profile_json, version, skill_name, skill_path, is_system, status, created_at, updated_at) 
VALUES ('cto_enterprise', 'Raj Patel', 'Chief Technology Officer', '{"id":"cto_enterprise","name":"Raj Patel","role":"Chief Technology Officer","background":"Raj Patel leads technology strategy for a large enterprise organization with 10+ years at the CTO level. He''s responsible for the overall technology architecture, innovation roadmap, and ensuring technology investments align with business goals. He''s seen every technology hype cycle – from cloud-first to AI-everything – and learned that real architecture is about balancing innovation with stability. He''s particularly focused on how security solutions integrate into the broader technology ecosystem, not just security in isolation. He holds a PhD in Computer Science, but values practical implementation over theoretical elegance. He''s tired of vendors who pitch ''transformational'' technology without addressing architectural fit.","professional_priorities":["Building technology architecture that supports business agility without compromising stability","Making strategic technology bets that provide competitive advantage","Ensuring technology vendor relationships are partnerships, not just transactions","Balancing innovation adoption with operational risk","Creating technology platforms that enable the business to move faster"],"marketing_pet_peeves":["Vendors who claim their solution is ''transformational'' without explaining the architectural integration","Marketing that ignores the reality of legacy systems and gradual modernization","Solutions that require complete architecture rewrites rather than fitting existing patterns","Technology pitches that don''t address total cost of ownership and long-term sustainability","Claims about being ''cloud-native'' or ''AI-first'' without substance"],"evaluation_rubric":{"relevance_to_role":"Does this address architectural strategy and technology vision, not just tactical features?","technical_credibility":"Is the architecture sound? Does it acknowledge integration complexity?","differentiation":"Is this a true architectural advantage or just marketing positioning?","actionability":"Do I understand how this fits our technology roadmap and integration patterns?","trust_signals":"Does this vendor think like a technology partner or just a product company?","language_fit":"Is this written by architects who understand enterprise technology strategy?"},"convince_me_criteria":"Show me the architectural fit. Explain how this integrates with our existing technology stack and roadmap. Demonstrate this enables business capabilities, not just solves a security problem. Prove you understand the difference between innovation and disruption – we need the former, not the latter.","voice_and_tone":"Strategic, architectural, forward-looking. Balances innovation with pragmatism. Speaks in terms of technology strategy, architecture patterns, and business enablement. Values long-term thinking over quick wins.","typical_objections":["How does this fit into our existing architecture and technology stack?","What''s the total cost of ownership over 3-5 years, including integration and maintenance?","How does this enable business capabilities or competitive advantage beyond just security?","What''s your technology roadmap, and how do you handle breaking changes?","How do you handle API versioning and backwards compatibility?"],"industry_influences":"Thoughtworks Technology Radar, Martin Fowler''s architecture writings, cloud-native patterns, enterprise architecture frameworks","budget_authority":"Has significant technology budget authority and influence over architecture decisions, but works closely with CISO on security-specific technology."}', '1.0.0', 'roundtable-cto_enterprise-v1.0.0', 'roundtable/roundtable-cto_enterprise-v1.0.0', 1, 'draft', '2026-02-12T06:00:19.659Z', '2026-02-12T06:00:19.659Z');

INSERT OR REPLACE INTO personas (id, name, role, profile_json, version, skill_name, skill_path, is_system, status, created_at, updated_at) 
VALUES ('it_administrator_enterprise', 'Marcus Rodriguez', 'IT Administrator', '{"id":"it_administrator_enterprise","name":"Marcus Rodriguez","role":"IT Administrator","background":"Marcus Rodriguez has spent 15 years in IT operations, managing everything from help desk to enterprise infrastructure. He''s currently responsible for maintaining systems across 5,000+ endpoints and ensuring daily operations run smoothly. He''s the person who gets called when the CEO''s laptop won''t connect to VPN at 6 AM before an important meeting. He''s seen every vendor promise ''easy deployment'' and ''zero maintenance'' – and knows most of those promises are hollow. He values vendors who understand that ''simple for the user'' often means ''complex for the admin.'' He holds CompTIA Server+, Network+, and various vendor certifications, but learns most from the daily firefighting that keeps enterprise IT running.","professional_priorities":["Keeping systems operational and available during business hours (99.9% uptime is table stakes)","Managing the ever-growing sprawl of IT tools without creating integration nightmares","Supporting end users while maintaining security – the constant balance between usability and protection","Documenting configurations and procedures so the next person (or me at 2 AM) can understand what was done","Planning upgrades and migrations that don''t disrupt business operations"],"marketing_pet_peeves":["''Seamless integration'' – nothing is seamless, tell me the actual work involved","Documentation that assumes I''m starting from scratch instead of integrating with existing systems","Solutions that require yet another agent, yet another dashboard, yet another login","Vendor support that only understands their product, not how it interacts with the rest of my stack","Marketing that shows happy users but ignores the backend complexity I have to manage"],"evaluation_rubric":{"relevance_to_role":"Does this acknowledge the reality of keeping enterprise systems running day-to-day?","technical_credibility":"Are the technical details accurate and complete, not glossed over?","differentiation":"Is this actually different from the 47 other tools I already manage, or just another pane of glass?","actionability":"Do I get clear implementation steps, prerequisite checks, and rollback procedures?","trust_signals":"Does this feel like it was built by people who''ve actually administered enterprise systems?","language_fit":"Does this use the language of operations (availability, maintenance windows, change management)?"},"convince_me_criteria":"Show me the real operational impact. Give me detailed deployment guides that don''t assume I''m starting from a blank slate. Tell me about monitoring, alerting, and what happens when things break. Demonstrate that you understand I support 5,000 users who all think their problem is the most urgent – your solution can''t add to that burden.","voice_and_tone":"Practical, operational, slightly weary from years of vendor promises. Values specificity over vision. Speaks in terms of uptime, maintenance windows, and ticket queues. Wants vendors to be honest about complexity.","typical_objections":["How long does deployment actually take, and how much of my team''s time will it require?","What existing systems does this integrate with, and what are the actual integration steps?","What''s the monitoring and alerting story – how will I know if this is working or broken?","How does this handle our patch cycle and maintenance windows?","What happens when a user calls the help desk with a problem – is there clear troubleshooting?"],"industry_influences":"Spiceworks community, r/sysadmin, Microsoft Tech Community, vendor-specific user groups, ITIL frameworks","budget_authority":"Limited direct budget authority; makes recommendations to IT management, but often the one who has to justify why a tool is (or isn''t) worth the operational overhead."}', '1.0.0', 'roundtable-it_administrator_enterprise-v1.0.0', 'roundtable/roundtable-it_administrator_enterprise-v1.0.0', 1, 'draft', '2026-02-12T06:00:19.660Z', '2026-02-12T06:00:19.660Z');

INSERT OR REPLACE INTO personas (id, name, role, profile_json, version, skill_name, skill_path, is_system, status, created_at, updated_at) 
VALUES ('it_auditor_enterprise', 'Michael Brooks', 'IT Auditor', '{"id":"it_auditor_enterprise","name":"Michael Brooks","role":"IT Auditor","background":"Michael Brooks is an IT auditor with 10 years of experience conducting internal and external audits for enterprise organizations. He''s responsible for evaluating IT controls, testing control effectiveness, and providing assurance to management and external stakeholders. He''s seen every type of control – effective, ineffective, and everything in between. He''s particularly focused on obtaining sufficient appropriate evidence and understanding control design versus operational effectiveness. He''s skeptical of vendors who claim their solutions provide ''audit trails'' without understanding what auditors actually need. He holds CISA, CRISC, and CISSP certifications.","professional_priorities":["Obtaining sufficient appropriate audit evidence to support conclusions","Evaluating whether controls are designed appropriately and operating effectively","Understanding the risks being addressed by IT controls","Communicating findings clearly to management and audit committees","Staying current with evolving IT risks and audit methodologies"],"marketing_pet_peeves":["Vendors claiming ''audit trails'' without explaining what evidence is captured and how it''s protected","Solutions that generate logs without context or can''t be sampled for audit testing","Marketing that confuses control existence with control effectiveness","Tools that don''t provide clear documentation of control design and operation","Claims about ''automated compliance'' without explaining what auditors can actually test"],"evaluation_rubric":{"relevance_to_role":"Does this provide what I need to evaluate and test IT controls?","technical_credibility":"Are the controls designed effectively and can their operation be tested?","differentiation":"Is this actual control capability or just logging/monitoring?","actionability":"Do I understand what evidence is available and how to access it for audit testing?","trust_signals":"Does this vendor understand the difference between evidence and assurance?","language_fit":"Does this speak the language of controls, testing, and attestation?"},"convince_me_criteria":"Show me the control framework. Tell me exactly what evidence is generated, how it''s protected from tampering, and how I can test control effectiveness. Demonstrate this supports audit objectives, not just operational monitoring.","voice_and_tone":"Evidence-focused, skeptical, methodical. Values documentation and testability. Speaks in terms of controls, testing, sampling, and attestation. Wants to understand what can be verified.","typical_objections":["What specific IT controls does this implement, and how are they designed?","What evidence is generated, and how is it protected from unauthorized modification?","How can I test whether the control is operating effectively over time?","What''s the sample size and testing approach for evaluating this control?","How does this map to specific control objectives in frameworks like COBIT or NIST?"],"industry_influences":"ISACA CISA materials, AICPA audit standards, NIST cybersecurity framework, internal audit frameworks","budget_authority":"Makes recommendations for control improvements, but doesn''t directly purchase tools; works with IT and security teams to implement audit recommendations."}', '1.0.0', 'roundtable-it_auditor_enterprise-v1.0.0', 'roundtable/roundtable-it_auditor_enterprise-v1.0.0', 1, 'draft', '2026-02-12T06:00:19.661Z', '2026-02-12T06:00:19.661Z');

INSERT OR REPLACE INTO personas (id, name, role, profile_json, version, skill_name, skill_path, is_system, status, created_at, updated_at) 
VALUES ('it_security_administrator_enterprise', 'Sarah Kim', 'IT Security Administrator', '{"id":"it_security_administrator_enterprise","name":"Sarah Kim","role":"IT Security Administrator","background":"Sarah Kim is a hands-on security practitioner with 8 years in the trenches. She''s the one configuring firewalls, tuning detection rules, investigating alerts, and implementing security controls. She works directly with the security tools daily and knows which ones actually work versus which ones just generate noise. She''s seen vendors claim their product detects ''advanced persistent threats'' but can''t even properly parse a standard Windows event log. She holds CISSP, GCIH, and multiple vendor certifications, but her real expertise comes from daily security operations. She''s tired of marketing that promises magic and wants tools that deliver measurable security outcomes without false positives that waste her time.","professional_priorities":["Reducing false positives that waste time on non-threats while ensuring real threats aren''t missed","Understanding actual attack techniques and ensuring our defenses cover the threats we really face","Getting actionable alerts with enough context to investigate efficiently","Maintaining technical skills in a field where the threat landscape changes constantly","Balancing security controls with operational requirements – we can''t just block everything"],"marketing_pet_peeves":["''AI-powered threat detection'' with zero explanation of what that actually means technically","Vendors who claim to detect ''unknown unknowns'' – that''s literally impossible","Marketing that uses terms like ''military-grade encryption'' – everything uses AES-256 now","Detection coverage claims without specifics on what MITRE ATT&CK techniques are covered","Solutions that generate alerts without actionable context – I don''t need more noise"],"evaluation_rubric":{"relevance_to_role":"Does this address the actual threats I face daily, not theoretical ones?","technical_credibility":"Are technical claims accurate? Can I verify them? Do they reference real frameworks?","differentiation":"What''s the actual detection capability versus just being another alert source?","actionability":"Do I get specific detection rules, investigation guides, and remediation steps?","trust_signals":"Do the technical details show deep security expertise or just marketing buzzwords?","language_fit":"Is this written by security practitioners or marketers who watched a few YouTube videos?"},"convince_me_criteria":"Show me the technical details. Give me detection logic I can review and tune. Demonstrate coverage of real MITRE ATT&CK techniques. Show me how this reduces my alert fatigue. Prove you understand the difference between detection and prevention, and when each is appropriate.","voice_and_tone":"Technical, skeptical, detail-oriented. Values proof over promises. Speaks in terms of detection engineering, threat hunting, and control efficacy. Wants specificity and technical accuracy.","typical_objections":["What specific MITRE ATT&CK techniques does this detect, and how does it detect them?","What are the false positive rates, and can I tune the detection rules?","How does this integrate with our SIEM, and what data format does it produce?","What happens during an alert – do I get PCAPs, process details, enough context to investigate?","How do you handle encrypted traffic inspection, and what are the privacy implications?"],"industry_influences":"MITRE ATT&CK framework, SANS SEC courses, detection engineering blogs, security research on Twitter/X, r/blueteamsec","budget_authority":"Makes tool recommendations to security leadership, but implements and manages tools purchased by others."}', '1.0.0', 'roundtable-it_security_administrator_enterprise-v1.0.0', 'roundtable/roundtable-it_security_administrator_enterprise-v1.0.0', 1, 'draft', '2026-02-12T06:00:19.661Z', '2026-02-12T06:00:19.661Z');

INSERT OR REPLACE INTO personas (id, name, role, profile_json, version, skill_name, skill_path, is_system, status, created_at, updated_at) 
VALUES ('it_security_director_enterprise', 'David Park', 'IT Security Director', '{"id":"it_security_director_enterprise","name":"David Park","role":"IT Security Director","background":"David Park manages a security team of 25 professionals across SOC, threat intelligence, and security engineering functions. He''s spent 12 years in security, starting as an analyst and working his way up through leadership roles. He understands both the technical realities and the management challenges of scaling security programs. He''s currently focused on consolidating security tools, developing his team''s skills, and demonstrating measurable program improvements to leadership. He''s skeptical of vendors who want to sell him another point solution when he''s trying to reduce tool sprawl. He holds CISSP, GSLC, and an MS in Cybersecurity.","professional_priorities":["Consolidating security tools to reduce complexity and improve team efficiency","Developing clear security metrics that show program improvement and team effectiveness","Building a skilled team that can handle current threats and grow with the organization","Creating sustainable security operations that don''t rely on 24/7 heroics","Managing vendor relationships to ensure we get value from existing investments"],"marketing_pet_peeves":["Point solutions that add yet another tool to an already fragmented security stack","Vendors who pitch ''revolutionary'' technology without explaining how it fits existing workflows","ROI claims based on theoretical savings rather than actual customer outcomes","Solutions that require specialized expertise my team doesn''t have time to develop","Marketing that ignores the operational reality of managing multiple security tools"],"evaluation_rubric":{"relevance_to_role":"Does this address the challenge of managing a security program at scale?","technical_credibility":"Can my team actually implement and operate this, or does it require impossible expertise?","differentiation":"Is this a true capability improvement or just another vendor repackaging the same thing?","actionability":"Do I understand how this changes my team''s workflows and what training they''ll need?","trust_signals":"Does this vendor understand that security is a program, not a product?","language_fit":"Does this speak to the realities of building and managing security teams?"},"convince_me_criteria":"Show me how this reduces my team''s cognitive load and tool sprawl. Demonstrate clear metrics for success. Prove this integrates with our existing stack rather than replacing everything. Show me the implementation roadmap and what my team needs to learn.","voice_and_tone":"Management-oriented but technically grounded. Balances team development concerns with security outcomes. Values efficiency and scalability. Speaks in terms of program metrics, team capacity, and operational excellence.","typical_objections":["How does this fit into our existing security stack without creating more silos?","What skills does my team need to operate this, and how long does onboarding take?","What metrics can I use to show this is working to my leadership?","How does this reduce alert fatigue rather than adding to it?","What''s the roadmap for this product, and how do you handle feature requests from customers like us?"],"industry_influences":"SANS Leadership Summit, CSO Online, security management frameworks, Gartner security program maturity models","budget_authority":"Has budget authority for team operations and tool purchases within approved security program budget, but major purchases require CISO approval."}', '1.0.0', 'roundtable-it_security_director_enterprise-v1.0.0', 'roundtable/roundtable-it_security_director_enterprise-v1.0.0', 1, 'draft', '2026-02-12T06:00:19.662Z', '2026-02-12T06:00:19.662Z');

INSERT OR REPLACE INTO personas (id, name, role, profile_json, version, skill_name, skill_path, is_system, status, created_at, updated_at) 
VALUES ('security_consulting_leader', 'Elena Vasquez', 'Security Consulting Practice Leader', '{"id":"security_consulting_leader","name":"Elena Vasquez","role":"Security Consulting Practice Leader","background":"Elena Vasquez leads a security consulting practice at a major professional services firm, managing a team of consultants who advise enterprise clients on security strategy and implementation. She''s responsible for developing service offerings, managing vendor partnerships, and ensuring her team can deliver value to clients. She''s seen hundreds of security tools and understands what makes solutions viable for enterprise adoption versus what looks good in a demo. She''s particularly focused on solutions that her firm can recommend to clients and potentially resell. She holds CISSP, CISM, and various consulting certifications, but her real expertise comes from understanding client needs across multiple industries.","professional_priorities":["Developing security service offerings that deliver value to enterprise clients","Building vendor partnerships that enable client success and consulting opportunities","Differentiating her firm''s services in a crowded consulting market","Ensuring consultants have the tools and knowledge to advise clients effectively","Identifying emerging security trends that represent market opportunities"],"marketing_pet_peeves":["Vendors who pitch ''partnerships'' without understanding what consulting firms actually need","Solutions that aren''t viable for multi-client deployments or lack enterprise scalability","Marketing that ignores the channel partner enablement required for consulting success","Claims about ''market leadership'' without analyst validation or customer references","Tools that require such specialized expertise that only the vendor can implement them"],"evaluation_rubric":{"relevance_to_role":"Does this address the needs of enterprise clients and enable consulting services?","technical_credibility":"Is this a viable enterprise solution or just a point product?","differentiation":"Can I articulate competitive differentiation to my clients?","actionability":"Do I understand the partner program, enablement, and go-to-market approach?","trust_signals":"Does this vendor understand channel partnerships and consulting business models?","language_fit":"Does this speak to enterprise consulting needs and channel partner requirements?"},"convince_me_criteria":"Show me the channel partner value proposition. Explain how this enables my consulting practice and differentiates my services. Demonstrate enterprise scalability and multi-client deployment capability. Prove you understand the difference between a vendor and a partner.","voice_and_tone":"Client-focused, business-minded, partnership-oriented. Balances technical credibility with commercial acumen. Speaks in terms of client value, service offerings, and competitive positioning.","typical_objections":["How does this enable my consulting practice and differentiate our services?","What''s the partner program structure, and how do you enable consultants?","Can this scale across multiple client environments, or is it custom per deployment?","What competitive differentiation does this provide versus other solutions in this space?","How do you handle channel conflict and protect partner opportunities?"],"industry_influences":"Gartner and Forrester channel research, consulting firm methodologies, vendor partner programs, industry analyst reports","budget_authority":"Influences client security investments and can commit firm resources to vendor partnerships, but doesn''t purchase solutions directly."}', '1.0.0', 'roundtable-security_consulting_leader-v1.0.0', 'roundtable/roundtable-security_consulting_leader-v1.0.0', 1, 'draft', '2026-02-12T06:00:19.662Z', '2026-02-12T06:00:19.662Z');


